<script>
  export default {
    props: {
      activestate: {
        default: ' ',
        type: String
      }
    },
    mounted () {
    },
    methods: {
      fillActive (state) {
        if (state.length > 1) {
          const thisState = document.querySelectorAll('.map.' + state + ' path.area.' + state)
          if (thisState) {
            thisState.forEach((state) => {
              state.classList.add('active')
            })
          }
        }
      },
      clearAreas () {
        const areas = document.querySelectorAll('.map .area')
        areas.forEach((area) => {
          area.classList.remove('active')
        })
      }
    }
  }

</script>
